mapper.insert:
INSERT
	  INTO TBL_BURGER_MENU A
 	(
 	  A.BURGER_MENU_CODE
 	, A.BURGER_MENU_NAME
 	, A.BURGER_MENU_BRAND
 	, A.BURGER_MENU_PRICE
 	, A.BURGER_MENU_RANK
 	, A.USER_NO
 	)
 	VALUES
 	(
 	  SEQ_BURGER_MENU_CODE_NEXTVAL
 	, #{ menuName }
 	, #{ menuBrand }
 	, #{ menuPrice }
 	, #{ menuRank }
 	, #{ userNo }
 	)

mapper.update:
UPDATE
		TBL_BURGER_MENU A
	SET A.BURGER.MENU_NAME = # { menuName }
	  , A.BURGER.MENU_BRAND = # { menuBrand }
	  , A.BURGER.MENU_PRICE = # { menuPrice }
	  , A.BURGER.MENU_RANK = # { menuRank }
 WHERE A.BURGER_MENU_CODE = # { menuCode }

mapper.delete:
DELETE
  FROM TBL_BURGER_MENU A
 WHERE A.BURGER_MENU_CODE = { menuCode }
