 , A.MENU_BRAND
 , A.MENU_NAME
 , A.MENU_PRICE

 , A.MENU_RANK

  )
  VALUES
 (
  SEQ_MENU_CODE.NEXTVAL
, #{title}
, #{content}
, #{menuBrand}
, #{menuName}
, #{menuPrice}
, #{menuRank} 
 )

mapper.update:
